import List_64 from './components/List_64'
import Alert_64 from './components/Alert_64'

const App_64 = () => {
  const [name, setName] = useState('');
  const [list, setList] = useState([]);
  const [alert, setAlert] = useState({
    show: false,
    msg: '',
    type: ''
});

const handleSubmit = (e) => {
  e.preventDefault();
  if (!name){
    //
  } else {
    const newItem = {
      id: new Date().getTime().toString(),
      title: name
    };
    setList(...list, newItem);
  }
}

  return (
    <>
      <section className="section-center">
        <form className="grocery-form" onsubmit={handleSubmit}>
          <h3>Grocery Bud - 208410364</h3>
          <div className="form-control">
            <input type="text" className="grocery" placeholder='e.g. eggs' value={name} onChange={
              (e) => {setName(e.target.value)}}/>
            <button type='submit' className='submit-btn'>submit</button>
          </div>
        </form>
      </section>
    </>
  )
}

export default App_64
